package Week1Day1operators;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int A = 50;
		int B = 12;
		float C = 24.08f;
		float D = 5.6f;
		System.out.println("Addition of two whole numbers is: "+(A+B));
		System.out.println("Addition of two Decimal numbers is: "+(C+D));
		System.out.println("Addition of one whole and one decimal number is: "+(A+D));
		System.out.println("Addition of one Decimal and one whole number is: "+(C+B));

		System.out.println("Substraction of two whole numbers is: "+(A-B));
		System.out.println("Substraction of two Decimal numbers is: "+(C-D));
		System.out.println("Substraction of one whole and one decimal number is: "+(A-D));
		System.out.println("Substraction of one Decimal and one whole number is: "+(C-B));

		System.out.println("Multiplication of two whole numbers is: "+(A*B));
		System.out.println("Multiplication of two Decimal numbers is: "+(C*D));
		System.out.println("Multiplication of one whole and one decimal number is: "+(A*D));
		System.out.println("Multiplication of one Decimal and one whole number is: "+(C*B));

		System.out.println("Division of two whole numbers is: "+(A/B));
		System.out.println("Division of two Decimal numbers is: "+(C/D));
		System.out.println("Division of one whole and one decimal number is: "+(A/D));
		System.out.println("Division of one Decimal and one whole number is: "+(C/B));

		System.out.println("Modulo of two whole numbers is: "+(A%B));
		System.out.println("Modulo of two Decimal numbers is: "+(C%D));
		System.out.println("Modulo of one whole and one decimal number is: "+(A%D));
		System.out.println("Modulo of one Decimal and one whole number is: "+(C%B));

	}
}
