package Week1Day1operators;

public class RelationalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int A=10;
		int B=15;
		int C=10;
		System.out.println("Validate if A is greater than B :"+(A>B));
		System.out.println("Validate if A is then than B :"+(A<B));
		System.out.println("Validate if A is equal to C :"+(A=C));
		System.out.println("Validate if A is not equal to C :"+(A!=C));
		System.out.println("Validate if A is greater than or equal to C :"+(A>=C));
		System.out.println("Validate if B is less than or equal to C :"+(B<=C));
		

	}

}
