package Week1Day1operators;

public class UnaryOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int A=1;
		int B=6;
		int c=10;
		System.out.println("Pre incremet operator ++A is:"+(++A));
		System.out.println("Post incremet operator A++ is:"+(A++));
		System.out.println("new value of variable A is:"+A);

		System.out.println("Pre decremet operator ++B is:"+(--B));
		System.out.println("Post decremet operator B++ is:"+(B--));
		System.out.println("new value of variable B is:"+B);

	}

}
