package week1day1controlfolw;

public class IfElseNestedFlow {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int A= 10;
				int B= 20;
				int C= 30;
				
			if(B<C)
			{
				if (A>C)
				{
					System.out.println("A+B"+(A+B));
				}
				else
				{
					System.out.println("C-B : "+(C-B));
				}
			}

	}

}
